import React, { useEffect, useState } from 'react';
import api from './services/api';

import './global.css';
import './App.css';
import './Sidebar.css';
import './Main.css';

import DevItem from './components/DevItem'
import DevAdd from './components/DevForm/DevAdd'
import DevUpdate from './components/DevForm/DevUpdate'

function App() {
  const [devs, setDevs] = useState([]);
  const [dates, setDates] = useState([]);
  const [editing, setEditing] = useState(false);
  const initialFormState = { id: null, name: '', username: '' }
  const [currentUser, setCurrentUser] = useState(initialFormState)

  useEffect(() => {
    async function loadDevs() {
      const response = await api.get('/devs');
      setDevs(response.data);
    }
    loadDevs();
  }, [])

  async function handleAddDev(data) {
    const response = await api.post('/devs', data)
    setDevs([...devs, response.data]);
  }

  const editRow = user => {
    setEditing(true)
    setCurrentUser({ id: user.id, name: user.name, username: user.username });
  }

  async function handleUpdateDev(github_username) {
    setEditing(true)
    //await api.put(`/update?github_usernam=${github_username}`);
    setCurrentUser({ github_username: '123', bio: '456' })

    //const AtualizaDevs = devs.filter(element => element.github_username !== github_username);
    //setDevs(AtualizaDevs);
  }

  async function handleDeleteDev(github_username) {
    await api.delete(`/delete?github_username=${github_username}`)
    const AtualizaDevs = devs.filter(element => element.github_username !== github_username);
    setDevs(AtualizaDevs);
  }


  return (
    <div id="app">

      <aside>
        <strong>Cadastrar</strong>
        <DevAdd handleAddDev={handleAddDev} >
        </DevAdd>
      </aside>

      <main>
        <ul>
          {devs.map(dev => (
            <DevItem key={dev._id} dev={dev} handleDeleteDev={handleDeleteDev} handleUpdateDev={editRow} />
          ))}
        </ul>
      </main>
    </div>
  );

}

export default App;
