import React, { useEffect, useState } from 'react';
import api from './services/api';

import './global.css';
import './App.css';
import './Sidebar.css';
import './Main.css';

import DevItem from './components/DevItem'
import DevForm from './components/DevForm'

function App() {

  const [devs, setDevs] = useState([]);
  const [dates, setDates] = useState([]);

  useEffect(() => {
    async function loadDevs() {
      const response = await api.get('/devs');
      setDevs(response.data);
    }
    loadDevs();
  }, [])

  async function handleAddDev(data) {
    const response = await api.post('/devs', data)
    setDevs([...devs, response.data]);
  }

  async function handleCrudDev(github_username, acao, nome, bio, techs, Save) {
    console.log('1: -> ', acao);
    if (acao === 'delete') {
      await api.delete(`/delete?github_username=${github_username}`)
      const devs2 = devs.filter(element => element.github_username !== github_username);
      setDevs(devs2);
    } else if (acao === 'update') {

      if ((acao === 'update' && Save)) {
        await api.put(`/update?github_usernam=${github_username}&nome=${nome}&bio=${bio}&Techs=${techs}`);
      }
      const dates = devs.filter(element => element.github_username === github_username);
      dates[0].acao = 'update';
      setDates(dates[0]);
    }

  }


  return (
    <div id="app">

      <aside>
        <strong>Cadastrar</strong>
        <DevForm Dates={dates} onSubmit={handleAddDev} onSubmit2={handleCrudDev} />
      </aside>

      <main>
        <ul>
          {devs.map(dev => (
            <DevItem key={dev._id} dev={dev} onSubmit2={handleCrudDev} acao={dates[0]} />
          ))}
        </ul>
      </main>
    </div>
  );

}

export default App;
