import React from 'react';
import './styles.css'

function DevItem({ dev }, OnRemove) {

  function DeletarItem(atributo) {
    OnRemove(atributo);
  }

  return (<li className="dev-item">
    <header>

      <div className="HeaderFechar">
        <img src={dev.avatar_url} alt={dev.nome} />

        <button className="delete_item" onClick={() => DeletarItem(dev.github_username)}>
          ( X )
      </button>

      </div>

      <div className="user-info">
        <strong>
          {dev.nome}
        </strong>
        <span>
          {dev.techs.join(', ')}
        </span>
      </div>
    </header>
    <p>
      {dev.bio}
    </p>
    <a href={`https://github.com/${dev.github_username}`}>Acessar perfil no GitHub</a>
  </li >);
}

export default DevItem;
