import React, { useState, useEffect } from 'react';
import './styles.css'

function DevForm({ onSubmit, onSubmit2, Dates }) {

  const [github_username, setGithub_username] = useState('');
  const [techs, setTechs] = useState('');
  const [latitude, setLatitude] = useState('');
  const [longitude, setLongitude] = useState('');
  if (!Dates.acao) { Dates.acao = 'cad'; }
  if (!Dates.github_username) { Dates.github_username = ''; }
  if (!Dates.techs) { Dates.techs = ''; }
  let acao = '';
  useEffect(() => {

    navigator.geolocation.getCurrentPosition(
      (position) => {
        const { latitude, longitude } = position.coords;

        setLatitude(latitude);
        setLongitude(longitude);

      },
      (err) => {
        console.log(err);
      },
      {
        timeout: 30000,
      }
    )
  }, []);

  async function handleSubmit(e) {
    e.preventDefault();
    const bio = 'Bio';
    const nome = 'Nicholas';
    const Save = true;

    if (Dates.acao === 'update') {
      await onSubmit2(acao = 'update', github_username, nome, bio, techs, latitude, longitude, Save);
    } else if (Dates.acao === 'cad') {
      await onSubmit({
        github_username,
        techs,
        latitude,
        longitude,
      });
    }

    setGithub_username('');
    setTechs('');
  }
  return (
    <form onSubmit={handleSubmit} >
      <div className="input-block">
        <label htmlFor="github_username">Usu√°rio do Github</label>
        <input
          name="github_username"
          id="github_username"
          required
          value={Dates.github_username}
          onChange={e => setGithub_username(e.target.value)}
        />
      </div>

      <div className="input-block">
        <label htmlFor="techs">Tecnologias</label>
        <input
          name="techs"
          id="techs"
          required
          value={Dates.techs}
          onChange={e => setTechs(e.target.value)}
        />
      </div>

      <div className="input-group">
        <div className="input-block">
          <label htmlFor="latitude">Latitude</label>
          <input
            name="latitude"
            type="number"
            id="latitude"
            required
            value={latitude}
            onChange={e => setLatitude(e.target.value)}
          />
        </div>

        <div className="input-block">
          <label htmlFor="longitude">Longitude</label>
          <input
            name="longitude"
            type="number"
            id="longitude"
            required
            value={longitude}
            onChange={e => setLongitude(e.target.value)}
          />
        </div>
      </div>

      <button type="submit">Salvar</button>

    </form >
  );
}

export default DevForm;
