import React, { useState, useEffect } from 'react';
import './styles.css'

function DevForm({ onSubmit, onSubmit2, Dates }) {

  const [github_username, setGithub_username] = useState('');
  const [techs, setTechs] = useState('');
  const [latitude, setLatitude] = useState('');
  const [longitude, setLongitude] = useState('');
  if (!Dates.github_username) { Dates.github_username = ''; }
  if (!Dates.techs) { Dates.techs = ''; }
  useEffect(() => {

    navigator.geolocation.getCurrentPosition(
      (position) => {
        const { latitude, longitude } = position.coords;

        setLatitude(latitude);
        setLongitude(longitude);

      },
      (err) => {
        console.log(err);
      },
      {
        timeout: 30000,
      }
    )
  }, []);

  async function handleSubmit(e, acao) {
    e.preventDefault();
    var bio = 'BIO';
    var nome = 'Nome';

    if (acao[0].acao == 'update') {
      await onSubmit2(acao, github_username, {
        nome,
        bio,
        techs,
        latitude,
        longitude,
      });
    }

    await onSubmit({
      github_username,
      techs,
      latitude,
      longitude,
    });
    setGithub_username('');
    setTechs('');
  }
  return (
    < form onSubmit={handleSubmit} >
      <div className="input-block">
        <label htmlFor="github_username">Usu√°rio do Github</label>
        <input
          name="github_username"
          id="github_username"
          required
          value={Dates.github_username}
          onChange={e => setGithub_username(e.target.value)}
        />
      </div>

      <div className="input-block">
        <label htmlFor="techs">Tecnologias</label>
        <input
          name="techs"
          id="techs"
          required
          value={Dates.techs}
          onChange={e => setTechs(e.target.value)}
        />
      </div>

      <div className="input-group">
        <div className="input-block">
          <label htmlFor="latitude">Latitude</label>
          <input
            name="latitude"
            type="number"
            id="latitude"
            required
            value={latitude}
            onChange={e => setLatitude(e.target.value)}
          />
        </div>

        <div className="input-block">
          <label htmlFor="longitude">Longitude</label>
          <input
            name="longitude"
            type="number"
            id="longitude"
            required
            value={longitude}
            onChange={e => setLongitude(e.target.value)}
          />
        </div>
      </div>

      <button type="submit">Salvar</button>

    </form >
  );
}

export default DevForm;
