import React from 'react';
import './styles.css'

function DevItem({ dev, handleDeleteDev }) {

  async function handleDeleteDev(github_username) {
    await handleDeleteDev(github_username);
  }

  return (<li className="dev-item">
    <header>

      <div className="HeaderFechar">
        <img src={dev.avatar_url} alt={dev.nome} />

        <button className="editar_item" onClick={() => handleDeleteDev(dev.github_username)}>
          ( Editar )
        </button>

        <button className="delete_item" onClick={() => handleSubmit2(dev.github_username, { 'Acao': 'Delete' })}>
          ( X )
        </button>


      </div>

      <div className="user-info">
        <strong>
          {dev.nome}
        </strong>
        <span>
          {dev.techs.join(', ')}
        </span>
      </div>
    </header>
    <p>
      {dev.bio}
    </p>
    <a href={`https://github.com/${dev.github_username}`}>Acessar perfil no GitHub</a>
  </li >);
}

export default DevItem;
