import React, { useEffect, useState } from 'react';
import api from './services/api';

import './global.css';
import './App.css';
import './Sidebar.css';
import './Main.css';

import DevItem from './components/DevItem'
import DevForm from './components/DevForm'

function App() {
  const [devs, setDevs] = useState([]);
  const [dates, setDates] = useState([]);

  useEffect(() => {
    async function loadDevs() {
      const response = await api.get('/devs');
      setDevs(response.data);
    }
    loadDevs();
  }, [])

  async function handleAddDev(data) {
    const response = await api.post('/devs', data)
    setDevs([...devs, response.data]);
  }

  async function handleCrudDev(github_username, acao) {
    if (acao.Acao === 'Delete') {
      await api.delete(`/delete?github_username=${github_username}`)
      const AtualizaDevs = devs.filter(element => element.github_username !== github_username);
      setDevs([...devs, AtualizaDevs]);
    }
  }


  return (
    <div id="app">

      <aside>
        <strong>Cadastrar</strong>
        <DevForm Dates={dates} onSubmit={handleAddDev}>
        </DevForm>
      </aside>

      <main>
        <ul>
          {devs.map(dev => (
            <DevItem key={dev._id} dev={dev} onSubmit2={handleCrudDev} />
          ))}
        </ul>
      </main>
    </div>
  );

}

export default App;
