import React, { useEffect, useState } from 'react';
import api from './services/api';

import './global.css';
import './App.css';
import './Sidebar.css';
import './Main.css';

import DevItem from './components/DevItem'
import DevForm from './components/DevForm'

function App() {

  const [devs, setDevs] = useState([]);

  useEffect(() => {
    async function loadDevs() {
      const github_username = 'LiviaTadei';
      const response = await api.get('/devs');
      setDevs(response.data);
      const devs2 = devs.filter(element => element.github_username == github_username);
      console.log(devs2);
      setDevs(devs2);
    }
    loadDevs();
  }, [])

  async function handleAddDev(data) {
    const response = await api.post('/devs', data)
    setDevs([...devs, response.data]);
  }

  async function handleRemoveDev(github_username) {
    const response = await api.delete(`/delete?github_username=${github_username}`)
    //setDevs([...devs, response.data]);

  }

  return (
    <div id="app">

      <aside>
        <strong>Cadastrar</strong>
        <DevForm onSubmit={handleAddDev} />
      </aside>

      <main>
        <ul>
          {devs.map(dev => (
            <DevItem key={dev._id} dev={dev} onSubmit2={handleRemoveDev} />
          ))}
        </ul>
      </main>
    </div>
  );

}

export default App;
